import { ExoTemplate } from './recipe.model';
import { EventEmitter, Injectable } from '@angular/core';
import { Ingredient } from '../shared/ingredient.model';
import { SeanceTemplateService } from '../template-seance/seance-template.service';
import { Subject } from 'rxjs/Subject';

/** Manage exoTemplate */
@Injectable()
export class ExoTemplateService {

    exoTemplateChanged: Subject<ExoTemplate[]> = new Subject();

    private exoTemplate: ExoTemplate[] = [new ExoTemplate('Développé Couché', 'Exo de base',
        'data:image/jpeg;base64,',
        ['Pecs']
    ),
       new ExoTemplate('Squat', 'Exo de base',
        'data:image/jpeg;base64,',
         ['Jambe']
    )];

    constructor(private shopSvc: SeanceTemplateService) {}

    getExoTemplate() {
        return this.exoTemplate.slice(); // retourne une copie du tableau
    }

    getRecipe(id: number): ExoTemplate {
        return this.exoTemplate[id];
    }

    deleteExoTemplate(id: number) {
       this.exoTemplate.splice(id, 1);
       this.exoTemplateChanged.next(this.exoTemplate);
    }
    addToShoppingList(ings: Ingredient[]) {
        this.shopSvc.pushAllIngredients(ings);
    }

    addExoTemplate(recipe: ExoTemplate) {
        this.exoTemplate.push(recipe);
        this.exoTemplateChanged.next(this.exoTemplate);
    }
    updateExoTemplate(index: number, exo: ExoTemplate) {
        this.exoTemplate[index] = exo;
        this.exoTemplateChanged.next(this.exoTemplate);
    }

}
